class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [(i, 'h') for i in horizontalCut]
        for i in verticalCut:
            cuts.append((i, 'v'))
        cuts.sort(reverse=True)
        v = h = 0
        ans = 0
        for c, t in cuts:
            if t == 'h':
                ans += (v + 1) * c
                h += 1
            else:
                ans += (h + 1) * c
                v += 1
        return ans
