class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        int ver = 1,hor = 1;
        int i = h.length-1,j = v.length-1;
        long cost = 0;
        while(i >= 0 || j >= 0) {
            if(i >= 0 && j >= 0) {
                if(h[i] < v[j]) {
                    ver++;
                    cost += v[j] * hor;
                    j--;
                } else {
                    hor++;
                    cost += h[i] * ver;
                    i--;
                }
            } else if(i >= 0) {
                hor++;
                cost += h[i] * ver;
                i--;
            } else if(j >= 0) {
                ver++;
                cost += v[j] * hor;
                j--;
            }
        }
        return cost;
    }
}
