class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long cntV=0, cntH=0;
        long long ans=0;
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        while(cntV<n-1 && cntH<m-1)
        {
            if(h[cntH] < v[cntV])
            {
                ans += (cntH+1)*v[cntV];
                cntV++;
            }
            else
            {
                ans+=(cntV+1)*h[cntH];
                cntH++;
            }
        }
        while(cntV<n-1)
        {
            ans += (cntH+1)*v[cntV];
            cntV++;
        }

        while(cntH<m-1)
        {
            ans+=(cntV+1)*h[cntH];
            cntH++;
        }
        return ans;
    }
};
