class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<vector<int>> v;
        for (auto i : hc){
            v.push_back({i, 0});
        }
        for (auto i : vc){
            v.push_back({i, 1});
        }
        sort(v.begin(), v.end(), greater<vector<int>>());
        long long ans = 0;
        int vs = v.size();
        int cm = 0, cn = 0;
        for (int i = 0; i < vs; i++){
            if (v[i][1] == 0){
                cn++;
                ans += (cm + 1)*1ll*v[i][0];
            }
            else{
                cm++;
                ans += (cn + 1)*1ll*v[i][0];
            }
        }
        return ans;
    }
};
