class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long res = 0;
        int cntv = 1, cnth = 1;
        sort(hc.begin(), hc.end());
        sort(vc.begin(), vc.end());
        int i=hc.size()-1;
        int j=vc.size()-1;
        while(i>=0 && j>=0) {
            if(hc[i] * (cnth + 1) + vc[j] * cntv < hc[i] * cnth + vc[j] * (cntv + 1)) {
                res += vc[j] * cntv;
                j --;
                cnth ++;
            } else {
                res += hc[i] * cnth;
                cntv ++;
                i --;
            }
        }
        while(i>=0) {
            res += hc[i] * cnth;
            cntv ++;
            i --;
        }
        while(j>=0) {
            res += vc[j] * cntv;
            j --;
            cnth ++;
        }
        return res;
    }
};
