class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<int, int>> tot;
        for (auto i:hc) tot.push_back({i, 0});
        for (auto i:vc) tot.push_back({i, 1});
        sort(tot.begin(), tot.end());
        reverse(tot.begin(), tot.end());

        int h=1, v=1;
        long long int ans=0;
        for (auto i:tot){
            if (i.second){
                ans+=i.first*v;
                h+=1;

            }else{
                ans+=i.first*h;
                v+=1;

            }
            //cout<<i.second<<" "<<i.first<<" "<<h<<" "<<v<<'\n';
        }
        //cout<<"done for this"<<'\n';
        return ans;
    }
};
