class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;

        int cur = 0;
        int n = s.size();
        s.push_back('1');
        for(int i = 0; i<n; i++){
            if(s[i] == '0' && s[i+1] == '1'){
                ans+=cur;
            }
            if(s[i] == '1'){
                cur++;
            }
        }
        return ans;
    }
};
