class Solution {
public:
    int maxOperations(string s) {
        int ret = 0, cur = 0;
        int n = s.length();
        for(int i=0; i<n; ++i){
            if(s[i] == '1' && i > 0 && s[i-1] == '0'){
                ret += cur;
            }
            cur += (s[i] - '0');
        }
        if(s[n-1] == '0')   ret += cur;
        return ret;
    }
};
