class Solution{
  public:
  int maxOperations(string s){
    int i;
    long long c = 0;
    long long res = 0;
    for(i=(0);i<(s.size());i++){
      if(s[i] == '1'){
        c++;
        continue;
      }
      if(s[i+1] == '0'){
        continue;
      }
      res += c;
    }
    return res;
  }
}
