class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        long long presum = 0, res = 0;
        for(int i = 0; i < s.size(); i++) {
            if(s[i] == '0') continue;
            int j = i;
            while(j < s.size() && s[j] == '1') {
                j++;
            }
            res += presum;
            presum += (j - i);
            i = j - 1;
        }
        if(s[n - 1] == '0') res += presum;

        return res;
    }
};
