class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int i=n-1;

        int ones = 0;
        int zero = 0;
        int ops = 0;
        // while(i>=0 and s[i]!='0')i--;
        while(i>=0)
        {
            if(s[i]=='1')
            {
                // zero = 0;
                while(i>=0 and s[i]=='1')
                {
                    ones ++;
                    i--;
                }

                ops += ones*zero;
                ones=0;

            }
            else
            {
                if(s[i]=='0')
                {
                    zero++;
                    while(i>=0 and s[i]=='0')i--;
                }
            }
        }

        return ops;
    }
};
