class Solution {
public int maxOperations(String s) {
        int ans = 0;
        int count = 0;
        boolean hasZero = false;
        for (char c : s.toCharArray()) {
            if (c == '1') {
                if (hasZero) {
                    ans += count;
                    hasZero = false;
                }
                count++;
            } else {
                hasZero = true;
            }
        }
        return hasZero ? ans + count : ans;
    }
}
