class Solution {
    public int maxOperations(String s) {
        int max = 0;

        int beforeOne = 0;
        for (int i = 0; i <= s.length() - 2; i++) {
            if (s.charAt(i) == '1') {
                beforeOne++;

                if (s.charAt(i + 1) == '0') {
                    max += beforeOne;
                }
            }
        }

        return max;
    }
}
