class Solution {
    public int maxOperations(String s) {
        int n=s.length();
        int left=0;
        while(left<n && s.charAt(left)!='1') ++left;
        if(left==n || left==n-1) return 0;
        int ans=0;
        int pre1=0;
        for(int i=left;i<n;){
            while(i<n && s.charAt(i)=='1'){
                ++pre1;
                ++i;
            }
            if(i==n) return ans;
            ans+=pre1;
            while(i<n && s.charAt(i)=='0') ++i;
        }
        return ans;
    }
}
