class Solution {
    public int maxOperations(String s) {
        int count =0;
        int oneTemp = 0;
        int oneCount =0;
        boolean zeroFound = false;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                if(zeroFound){
                    count+=oneCount;
                }
                ++oneTemp;
                zeroFound = false;
            }

            else {
                oneCount+=oneTemp;
                oneTemp = 0;
                zeroFound = true;
            }
        }
        if(zeroFound)
            count+=oneCount;
        return count;
    }
}
