class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        op = cur = 0
        for i in range(n - 1, -1, -1):
            if s[i] == "0":
                continue
            nex = s[i + 1] if i < n - 1 else "1"
            if nex == "0":
                cur += 1
            op += cur
        return op
