class Solution {
public:
    int maxOperations(string s) {
        int tot = 0, ans = 0; 
        for(int i=0; i<s.size(); i++) { 
            tot += (s[i] == '1'); 
            bool yes = 0; 
            while(s[i] == '0' && i < s.size()) i++, yes = 1; 
            ans += tot * yes; i -= yes; 
        }
        return ans;
    }
};
