class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int ones = 0, n = s.size();
        for (int i = 0; i < n; ) {
            if (s[i] == '0') {
                ans += ones;
                while (i < n && s[i] == '0') ++i;
            } else {
                ++ones;
                ++i;
            }
        }
        return ans;
    }
};
