class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, cnt = 0;
        string t = "";
        t += s[0];
        for(int i = 1; i < s.size(); i++){
            if(s[i] == '0' && s[i - 1] == '0') continue;
            t += s[i];
        }
        for(char ch : t){
            if(ch == '0') ans += cnt;
            else cnt++;
        }
        return ans;
    }
};
