class Solution {
public:
    int maxOperations(string s) {
        int ans=0;

        int n=s.size();
        int cur=0;
        int one=0;
        for(int i=0;i<n;i++)
        {
            if(s[i]=='0')
            {
                if(cur)
                {
                    ans+=one;
                    cur=0;
                }
            }
            else
            {
                one++;
                cur++;
            }
        }

        return ans;

    }
};
