class Solution {
public:
    int maxOperations(string s) {
        int count=0;
        int ans=0;
        int a=false;
        for(int i=0;i<s.length();i++)
        {
            if(s[i]=='1')
            {
                count++;
                a=false;
            }
            while(s[i]=='0')
            {
                i++;
                a=true;
            }
            if(a)
            {
                i--;
                ans+=count;
            }
        }
        return ans;
    }
};
