class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), o = 0, ans = 0, z = 0, len = 0;
        for(int i=0; i<n; i++) {
            if(i==n-1 && s[i] == '0') {
                ans += len;
                break;
            }
            if(s[i] == '1') {
                if(len>0 && s[i-1] == '0') ans+=len;
                len++;
            }

        }
        return ans;
    }
};
