class Solution:
    def maxOperations(self, s: str) -> int:
        ret = 0

        n1s = 0

        i = 0
        n = len(s)

        while i < n:        
            if s[i] == '1':
                n1s += 1
            else:
                while i + 1 < n and s[i + 1] == '0':
                    i += 1
                ret += n1s
            i += 1

        return ret
