class Solution {
    public int maxOperations(String s) {
        int count = 0;
        int res = 0;
        for(int i = 0; i < s.length(); i++) {
            char c = s.charAt(i);
            if(c == '1') {
                count++;
            }else{
                if(i > 0 && s.charAt(i - 1) == '1') {
                    res += count;
                }
                while(i < s.length() && s.charAt(i) == '0') {
                    i++;
                }
                i--;
            }
        }
        return res;
    }
}
