impl Solution {
    pub fn max_operations(mut s: String) -> i32 {
        s.push('1');
        let mut ans = 0;
        let mut cn = 0;
        let mut k = 0;
        for c in s.chars() {
            if c == '1' {
                if k > 0 {
                    ans += cn;
                }

                cn += 1;
                k = 0;
            } else {
                k += 1;
            }
        }
        ans
    }
}
