# 每个1能移动的次数是右边的0组的个数
class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        res = 0
        groups = 0
        last = '1'
        for i in range(n-1, -1, -1):
            if s[i] == '0':
                if last == '1':
                    groups += 1
            else:
                res += groups
            # print(s[i], groups)
            last = s[i]
        return res
