class Solution {
    public int maxOperations(String s) {
        int n = s.length();
        int zeroSections = 0;
        int zeroCount = 0;
        int ans = 0;
        int index = n-1;
        while(index>=0) {
            if(s.charAt(index)=='0') {
                zeroCount++;
            } else {
                if(zeroCount != 0) {
                    zeroCount = 0;
                    zeroSections++;
                }
                ans+=zeroSections;
            }
            index--;
        }
        return ans;
    }
}
