class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        cur = 0
        pre = None
        for i in range(n - 1, -1, -1):
            if s[i] == '1':
                ans += cur
                pre = '1'
            else:
                if pre != '0':
                    cur += 1
                pre = '0'
        return ans
