class Solution:
    def maxOperations(self, s: str) -> int:
        n=len(s)
        res=0
        zero=False
        countZero=0
        for i in range(n-1,-1,-1):
            if s[i]=='1':
                if zero:
                    zero=False
                    countZero+=1
                res+=countZero
            else:
                zero=True
        return res
