class Solution:
    def maxOperations(self, s: str) -> int:
        ones = []
        for i, v in enumerate(s): 
            if v == '1': ones.append(i)
        n = len(ones)
        if n == 0: return 0
        dp = [0] * (n+5)
        if ones[-1] == len(s)-1: dp[n-1] = 0
        else: dp[n-1] = 1
        for i in reversed(range(n-1)):
            if ones[i] == ones[i+1]-1:
                dp[i] = dp[i+1]
            else:
                dp[i] = dp[i+1]+1

        ans = 0
        for d in dp: ans += d
        return ans
