class Solution {
public:
    int maxOperations(string s) {
        int result = 0, count = 0, flag = 0; 
        vector<int> segments; 
        for(int i = 0; i < s.size(); i++){ 
            if(s[i] == '1'){ 
                segments.push_back(1); 
                flag = 1; 
            } 
            if(s[i] == '0' && flag == 1){ 
                segments.push_back(0); 
                flag = 0; 
            } 
        } 
        for(auto segment : segments){ 
            if(segment == 1){ 
                count++; 
            } else { 
                result += count; 
            } 
        } 
        return result; 
    }
};
