class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        base = 0
        ans = 0
        for i in range(n - 1, -1, -1):
            if s[i] == "1":
                ans += base
            elif i == n - 1 or s[i + 1] == "1":
                base += 1

        return ans
