class Solution {
public:
    int maxOperations(string s) {
        int n=s.size();
        int ans=0;
        int count=0;
        for(int i=0; i<n; i++){
            if(s[i]=='1'){
                count++;
            }
            else if(i>0 && s[i]=='0' && s[i-1]=='1'){
                ans+=count;
            }
        }
        return ans;
    }
};
