class Solution:
    def maxOperations(self, s: str) -> int:
        ans=0
        n=len(s)
        g=0
        for i in range(n-1,-1,-1):
            if s[i]=='1':
                ans+=g
            else:
                if i+1==n or s[i+1]=='1':
                    g+=1
        return ans
