class Solution {
public:
    int maxOperations(string s) {
        int a=0;
        int c=0,f=0;
        for(auto x:s){
            if(x=='1'){
                if(f==0){a+=c;f=1;}
                c++;
            }
            else f=0;
        }
        if(!f)a+=c;
        return a;
    }
};
