class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        int k = 0;

        for (int i = 0; i < s.size() - 1; i++) {
            if (s[i] == '1' && s[i + 1] == '0') {
                k++;
                ret += k;
            } else if (s[i] == '1')
                k++;
        }

        return ret;
    }
};
