class Solution {
public:
    int maxOperations(string s) {

        int last = 0;
        int n =s.length();
        int ans = 0;
        int zero = false;
        for(int i = n - 1 ; i >= 0 ; i--){

            if(s[i] == '0'){
                zero = true;
            }
            else{
                if(zero){
                    ans += last + 1;
                    last++;
                }else{
                    ans += last;
                }
                zero = false;
            }

        }

        return ans;


    }
};
