class Solution:
    def maxOperations(self, s: str) -> int:
        arr = []
        res = 0
        stat = False
        cur = 0
        for ch in s:
            if ch == '1':
                if stat == False:
                    stat = True
                    cur = 1
                else:
                    cur += 1
            else:
                if stat == False:
                    continue
                else:
                    arr.append(cur)
                    stat = False
                    cur = 0
        #print(arr)
        if arr == []:
            return 0
        arr = arr[::-1]
        while arr:
            tmp = arr.pop()
            res += tmp
            if arr:
                arr[-1] += tmp
        return res
