class Solution:
    def maxOperations(self, s: str) -> int:
        # hold result
        res = 0
        # hold total number of 1s seen so far
        ones = 0
        for i in range(len(s) - 1):
            # update ones count if we see a 1
            if s[i] == '1':
                ones += 1
                # if next number is a 0, update result
                if s[i + 1] == '0':
                    res += ones
        return res
