class Solution {
    public int maxOperations(String s) {
        int count = 0, res = 0;
        boolean flag = true;
        for (char c : s.toCharArray()) {
            if (c == '1') {
                count++;
                flag = true;
            }
            else {
                if (flag) res += count;
                flag = false;
            }
        }
        return res;
    }
}
