#include <bits/stdc++.h>
using namespace std;
typedef long long ll;
typedef double db;
typedef pair<int, int> pii;

class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, res = 0, n = s.size();
        for (int i = 0; i < n;i++){
            if(s[i] == '1'){
                cnt += 1;
            } else if(s[i] == '0'){
                i++;
                for (; i < n;i++){
                    if(s[i] == '1') {
                        i--;
                        break;
                    }
                }
                res += cnt;
            }
        }

        return res;
    };
};
