class Solution {
public:
    int maxOperations(string s) {
        int counts = 0, ones = 0;
        for (int i = 0; i != s.size(); ++i) {
            if (s[i] == '1') {
                if (i && s[i - 1] == '0') {
                    counts += ones;
                }
                ++ones;
            }
        }
        if (s.back() == '0') {
            counts += ones;
        }
        return counts;
    }
};
