class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), ans = 0;

        int i = 0;
        while(i<n) {
            if(s[i] == '0') {
                i++;
                continue;
            }
            break;
        }

        int cnt1 = 0;

        while(i < n) {
            // cout<<i<<" "<<cnt1<<endl;
            if(i == n-1) {
                if(s[i] == '0') {
                    ans += cnt1;
                }
                break;
            }

            if(s[i+1] == '0') {
                // find the next 1
                int j = i+2;
                while(j<n && s[j] == '0') {
                    j++;
                }
                ans++;
                ans += cnt1;
                cnt1++;
                i = j;
            } else {
                // 10 not satisfied
                i++;
                cnt1++;
            }
        }

        return ans;
    }
};
