class Solution {
public:
    int maxOperations(string s) {
        deque<int> a;
        int sum = 0;
        for(int i=0, j =0; i<s.size(); i++) {
            if(s[i] == '0') continue;
            for(j = i; j<s.size() && s[j] == '1'; j++);
            a.push_back(j - i);   sum += j-i;
            i = j;
        }
        if(a.empty()) return 0;
        int ans = 0;
        while((int)a.size() > 1) {
            int x = a.front(); a.pop_front();
            ans += x;
            a.front() += x;
        }
        if(s.back() != '1') ans += a.front();
        return ans;
    }
};
