class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int ans = 0;
        for(int i=0;i<s.size();i++){
            if(s[i] == '1'){
                ans += ones;
                ones++;
                i++;
                while(i<s.size() && s[i] == '1'){
                    i++;
                    ones++;
                }
                if(i == s.size()){
                    break;
                }
            }
        }
        if(s[s.size()-1] == '0'){
            ans += ones;
        }
        return ans;
    }
};
