class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        bool mov = false;
        int n1 = 0;

        for (char c : s) {
            if (c == '1') {
                if (mov) {
                    mov = false;
                    ans += n1;
                }

                ++n1;
            } else {
                mov = true;
            }
        }
        if (s.back() == '0')
            ans += n1;
        return ans;
    }
};
