class Solution {
public:
    int maxOperations(string s) {
        int res = 0, i = 0, coun = 0, n = s.size();
        while(i < n) {
            if(s[i] == '1')coun++;
            else {
                res += coun;
                while(i < s.size() && s[i] == '0')i++;
                i--;
            }
            i++;
        }
        return res;
    }
};
