class Solution {
public:
    int maxOperations(string s) {
        int ans=0;
        int ones=0;
        int n=s.length();
        for (int i=0;i<n;i++){
            if (s[i]=='1') ones++;
            if (s[i]=='0'){
                if (i>0 && s[i-1]=='1'){
                    ans+=ones;
                }
            }
        }
        return ans;
    }
};
