class Solution {
public:
    int maxOperations(string s) {
        int co=0, ans=0;
        for(int i = s.size()-2; i > -1; i--){
            if(s[i]=='1' && s[i+1]=='0'){
                co++;
            }else if(s[i]=='1' && s[i+1]=='1' && co>0){
                ans+=co;
            }
        }
        for(int i = 1; i <= co; i++){
            ans+=i;
        }
        return ans;
    }
};
