class Solution:
    def maxOperations(self, s: str) -> int:
        count = 0
        op = 0
        flag = 1
        for i in range(len(s)):
            tmp = s[-1-i]
            if tmp == '0':
                if flag == 1:
                    op += 1
                    flag = 0
            else:
                count += op
                flag = 1
        return count
